    private List<Editorial> misEditoriales;
    bool preguntar = false;
    int idBorrar;

    protected override async Task OnInitializedAsync()
    {
        await Cargar();
    }

    private async Task Cargar()
    {
        misEditoriales = await http.GetFromJsonAsync<List<Editorial>>("api/editoriales");
    }


    private void Nuevo()
    {
        navegar.NavigateTo("editorialIndex/Crear");
    }


    private void Editar(int id)
    {
        navegar.NavigateTo($"editorialIndex/Editar/{id}");
    }
    private void preguntaEliminar(int id)
    {
        preguntar = true;
        idBorrar = id;
    }

    public async Task Borrar()
    {
        await http.DeleteAsync($"api/editoriales/{idBorrar}");
        await Cancelar();
        await Cargar();

    }
    private async Task Cancelar()
    {
        idBorrar = 0;
        preguntar = false;
        await Cargar();
    }

